import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Button,
  Box,
  Card,
  CardContent
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [projects, setProjects] = useState([]);
  const navigate = useNavigate();

  const fetchProjects = async () => {
    const customer_id = localStorage.getItem('customer_id');

    if (!customer_id) {
      alert('Please login first');
      navigate('/login');
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/projects/${customer_id}`);
      const data = await response.json();

      if (response.ok) {
        setProjects(data);
      } else {
        console.error('Failed to load projects:', data.error);
        alert('Could not load projects.');
      }
    } catch (error) {
      console.error('Error fetching projects:', error);
      alert('An error occurred while loading projects.');
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 6, mb: 2, display: 'flex', justifyContent: 'space-between' }}>
        <Typography variant="h4">Your Projects</Typography>
        <Button variant="contained" onClick={() => navigate('/create-project')}>
          Create New Project
        </Button>
      </Box>

      {projects.length === 0 ? (
        <Typography>No projects found.</Typography>
      ) : (
        projects.map((project) => (
          <Card key={project.trip_id} sx={{ mb: 2 }}>
            <CardContent>
              <Typography variant="h6">{project.trip_type || 'Untitled Project'}</Typography>
              <Typography>
                {project.start_date} â†’ {project.end_date}
              </Typography>
              <Typography variant="body2" color="textSecondary">
                Trip ID: {project.trip_id}
              </Typography>
            </CardContent>
          </Card>
        ))
      )}
    </Container>
  );
};

export default Dashboard;
