import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Card,
  CardContent,
  Grid,
  Button,
  Box,
  Divider
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [trips, setTrips] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const customer_id = localStorage.getItem('customer_id');
    if (!customer_id) {
      alert('Please login first');
      navigate('/login');
      return;
    }

    fetch(`http://localhost:5000/trips-with-members/${customer_id}`)
      .then((res) => res.json())
      .then((data) => setTrips(data))
      .catch((err) => {
        console.error('Failed to fetch trips:', err);
        alert('Error loading your projects');
      });
  }, [navigate]);

  const handleEdit = (trip_id) => {
    alert(`Edit trip ${trip_id} (edit logic to be implemented)`);
  };

  const handleDelete = async (trip_id) => {
    if (!window.confirm('Are you sure you want to delete this project?')) return;

    const res = await fetch(`http://localhost:5000/trips/${trip_id}`, {
      method: 'DELETE'
    });

    if (res.ok) {
      setTrips((prev) => prev.filter((trip) => trip.trip_id !== trip_id));
    } else {
      alert('Failed to delete project.');
    }
  };

  const formatDate = (dateString) =>
    new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>Your Projects</Typography>
      <Grid container spacing={3}>
        {trips.length === 0 ? (
          <Typography>No projects found. Click below to create your first one.</Typography>
        ) : (
          trips.map((trip) => (
            <Grid item xs={12} key={trip.trip_id}>
              <Card elevation={3}>
                <CardContent>
                  <Typography variant="h6">{trip.trip_type || 'Untitled Trip'}</Typography>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    {formatDate(trip.start_date)} → {formatDate(trip.end_date)}
                  </Typography>

                  <Divider sx={{ my: 1 }} />

                  <Typography variant="subtitle2">Invited Travellers:</Typography>
                  {trip.group_members.length > 0 ? (
                    trip.group_members.map((member, idx) => (
                      <Typography key={idx} variant="body2">
                        {member.name} ({member.email})
                      </Typography>
                    ))
                  ) : (
                    <Typography variant="body2" sx={{ mb: 1 }}>
                    {new Date(trip.start_date).toLocaleDateString('en-US', {
                      year: 'numeric', month: 'long', day: 'numeric'
                    })} → {new Date(trip.end_date).toLocaleDateString('en-US', {
                      year: 'numeric', month: 'long', day: 'numeric'
                    })}
                  </Typography>
                  
                  )}

                  <Box sx={{ mt: 2, display: 'flex', justifyContent: 'flex-end', gap: 2 }}>
                    <Button variant="outlined" color="primary" onClick={() => handleEdit(trip.trip_id)}>
                      Edit
                    </Button>
                    <Button variant="outlined" color="error" onClick={() => handleDelete(trip.trip_id)}>
                      Delete
                    </Button>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          ))
        )}
      </Grid>

      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 4 }}
        onClick={() => navigate('/create-project')}
      >
        Create New Project
      </Button>
    </Container>
  );
};

export default Dashboard;
