import { useState } from 'react';
import { TextField, MenuItem, Select, InputLabel, FormControl, Chip, Box, Typography, Button, IconButton, Alert } from '@mui/material';
import { Delete as DeleteIcon } from '@mui/icons-material';

function CreateProjectPage() {
  const [projectName, setProjectName] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [countries, setCountries] = useState([]);
  const [citiesByCountry, setCitiesByCountry] = useState({});
  const [travelers, setTravelers] = useState([]);
  const [errors, setErrors] = useState({});

  const availableCountries = ['Canada', 'USA', 'United Kingdom', 'Germany', 'France'];

  const availableCities = {
    'Canada': ['Toronto', 'Vancouver', 'Montreal', 'Calgary'],
    'USA': ['New York', 'Los Angeles', 'Chicago', 'Miami'],
    'United Kingdom': ['London', 'Manchester', 'Birmingham'],
    'Germany': ['Berlin', 'Munich', 'Frankfurt'],
    'France': ['Paris', 'Lyon', 'Marseille'],
  };

  const handleCountriesChange = (e) => {
    const selectedCountries = e.target.value;
    if (selectedCountries.length <= 10) {
      setCountries(selectedCountries);

      const updatedCities = { ...citiesByCountry };
      selectedCountries.forEach((country) => {
        if (!updatedCities[country]) {
          updatedCities[country] = [];
        }
      });
      Object.keys(updatedCities).forEach((country) => {
        if (!selectedCountries.includes(country)) {
          delete updatedCities[country];
        }
      });
      setCitiesByCountry(updatedCities);
    } else {
      setErrors(prev => ({ ...prev, countries: 'You can select up to 10 countries only.' }));
    }
  };

  const handleCityChange = (country, selectedCities) => {
    setCitiesByCountry(prev => ({
      ...prev,
      [country]: selectedCities,
    }));
  };

  const handleAddTraveler = () => {
    setTravelers([...travelers, { name: '', email: '', role: '' }]);
  };

  const handleTravelerChange = (index, field, value) => {
    const updated = [...travelers];
    updated[index][field] = value;
    setTravelers(updated);
  };

  const handleRemoveTraveler = (index) => {
    const updated = [...travelers];
    updated.splice(index, 1);
    setTravelers(updated);
  };

  const validateForm = () => {
    const newErrors = {};

    if (!projectName) newErrors.projectName = 'Project name is required.';
    if (!startDate) newErrors.startDate = 'Start date is required.';
    if (!endDate) newErrors.endDate = 'End date is required.';
    if (startDate && endDate && startDate > endDate) {
      newErrors.endDate = 'End date must be after start date.';
    }
    if (countries.length === 0) newErrors.countries = 'At least one country must be selected.';

    travelers.forEach((traveler, index) => {
      if (!traveler.name || !traveler.email || !traveler.role) {
        newErrors[`traveler-${index}`] = 'All traveler fields must be filled.';
      }
    });

    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      console.log('Form submitted!');
      // TODO: send data to Supabase later
      alert('Project saved successfully!');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Create a New Project</h1>

      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '16px', maxWidth: '700px' }}>
        
        <TextField
          label="Project Name"
          variant="outlined"
          value={projectName}
          onChange={(e) => setProjectName(e.target.value)}
          placeholder="Enter project name"
          error={!!errors.projectName}
          helperText={errors.projectName}
        />

        <TextField
          label="Start Date"
          type="date"
          InputLabelProps={{ shrink: true }}
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
          error={!!errors.startDate}
          helperText={errors.startDate}
        />

        <TextField
          label="End Date"
          type="date"
          InputLabelProps={{ shrink: true }}
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
          error={!!errors.endDate}
          helperText={errors.endDate}
        />

        <FormControl error={!!errors.countries}>
          <InputLabel>Countries</InputLabel>
          <Select
            multiple
            value={countries}
            onChange={handleCountriesChange}
            renderValue={(selected) => (
              <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                {selected.map((value) => (
                  <Chip key={value} label={value} />
                ))}
              </Box>
            )}
            style={{ width: '100%' }}
          >
            {availableCountries.map((country) => (
              <MenuItem key={country} value={country}>
                {country}
              </MenuItem>
            ))}
          </Select>
          {errors.countries && <Typography color="error" fontSize="small">{errors.countries}</Typography>}
        </FormControl>

        {countries.map((country) => (
          <div key={country}>
            <Typography variant="h6" style={{ marginTop: '20px' }}>{country} - Cities</Typography>

            <FormControl style={{ marginTop: '10px', width: '100%' }}>
              <InputLabel>Select Cities for {country}</InputLabel>
              <Select
                multiple
                value={citiesByCountry[country] || []}
                onChange={(e) => handleCityChange(country, e.target.value)}
                renderValue={(selected) => (
                  <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                    {selected.map((city) => (
                      <Chip key={city} label={city} />
                    ))}
                  </Box>
                )}
              >
                {availableCities[country].map((city) => (
                  <MenuItem key={city} value={city}>
                    {city}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </div>
        ))}

        {/* Travelers Section */}
        <Typography variant="h5" style={{ marginTop: '30px' }}>
          Travelers
        </Typography>

        {travelers.map((traveler, index) => (
          <Box key={index} display="flex" gap="8px" alignItems="center">
            <TextField
              label="Name"
              variant="outlined"
              value={traveler.name}
              onChange={(e) => handleTravelerChange(index, 'name', e.target.value)}
              error={!!errors[`traveler-${index}`]}
            />
            <TextField
              label="Email"
              variant="outlined"
              value={traveler.email}
              onChange={(e) => handleTravelerChange(index, 'email', e.target.value)}
              error={!!errors[`traveler-${index}`]}
            />
            <TextField
              label="Role"
              variant="outlined"
              value={traveler.role}
              onChange={(e) => handleTravelerChange(index, 'role', e.target.value)}
              error={!!errors[`traveler-${index}`]}
            />
            <IconButton onClick={() => handleRemoveTraveler(index)} color="error">
              <DeleteIcon />
            </IconButton>
          </Box>
        ))}

        <Button onClick={handleAddTraveler} variant="contained" style={{ marginTop: '10px' }}>
          Add Traveler
        </Button>

        <Button type="submit" variant="contained" color="primary" style={{ marginTop: '30px' }}>
          Save Project
        </Button>

      </form>
    </div>
  );
}

export default CreateProjectPage;
