import React, { useEffect, useState } from 'react';
import {
  Container, Typography, Card, CardContent, Grid, Button, Box,
  Divider, Dialog, DialogTitle, DialogContent, DialogActions,
  TextField, IconButton
} from '@mui/material';
import { AddCircle, RemoveCircle } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [trips, setTrips] = useState([]);
  const [editTrip, setEditTrip] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const customer_id = localStorage.getItem('customer_id');
    if (!customer_id) {
      alert('Please login first');
      navigate('/login');
      return;
    }

    fetch(`http://localhost:5000/trips-with-members/${customer_id}`)
      .then((res) => res.json())
      .then((data) => setTrips(data))
      .catch((err) => {
        console.error('Failed to fetch trips:', err);
        alert('Error loading your projects');
      });
  }, [navigate]);

  const handleEdit = (trip) => {
    setEditTrip({ ...trip, group_members: [...trip.group_members] });
  };

  const handleDelete = async (trip_id) => {
    if (!window.confirm('Are you sure you want to delete this project?')) return;

    const res = await fetch(`http://localhost:5000/trips/${trip_id}`, {
      method: 'DELETE'
    });

    if (res.ok) {
      setTrips((prev) => prev.filter((trip) => trip.trip_id !== trip_id));
    } else {
      alert('Failed to delete project.');
    }
  };

  const handleEditSave = async () => {
    const { trip_id, trip_name, start_date, end_date, group_members } = editTrip;

    try {
      const res = await fetch(`http://localhost:5000/trips/${trip_id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ trip_name, start_date, end_date, group_members })
      });

      if (res.ok) {
        const updated = trips.map((trip) =>
          trip.trip_id === trip_id ? { ...trip, trip_name, start_date, end_date, group_members } : trip
        );
        setTrips(updated);
        setEditTrip(null);
      } else {
        const err = await res.json();
        console.error('Failed to update project:', err);
        alert('Failed to update project.');
      }
    } catch (err) {
      console.error('Request error:', err);
      alert('Request failed');
    }
  };

  const formatDate = (date) =>
    new Date(date).toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>Your Projects</Typography>
      <Grid container spacing={3}>
        {trips.length === 0 ? (
          <Typography>No projects found. Click below to create your first one.</Typography>
        ) : (
          trips.map((trip) => (
            <Grid item xs={12} key={trip.trip_id}>
              <Card elevation={3}>
                <CardContent>
                  <Typography variant="h6">{trip.trip_name || 'Untitled Trip'}</Typography>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    {formatDate(trip.start_date)} → {formatDate(trip.end_date)}
                  </Typography>

                  <Divider sx={{ my: 1 }} />
                  <Typography variant="subtitle2">Invited Travellers:</Typography>
                  {trip.group_members.length > 0 ? (
                    trip.group_members.map((member, idx) => (
                      <Typography key={idx} variant="body2">
                        {member.name} ({member.email})
                      </Typography>
                    ))
                  ) : (
                    <Typography variant="body2" color="text.secondary">
                      No invited travellers.
                    </Typography>
                  )}

                  <Box sx={{ mt: 2, display: 'flex', justifyContent: 'flex-end', gap: 2 }}>
                    <Button variant="outlined" color="primary" onClick={() => handleEdit(trip)}>
                      Edit
                    </Button>
                    <Button variant="outlined" color="error" onClick={() => handleDelete(trip.trip_id)}>
                      Delete
                    </Button>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          ))
        )}
      </Grid>

      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 4 }}
        onClick={() => navigate('/create-project')}
      >
        Create New Trip
      </Button>

      {/* Edit Modal */}
      <Dialog open={!!editTrip} onClose={() => setEditTrip(null)} fullWidth maxWidth="sm">
        <DialogTitle>Edit Trip</DialogTitle>
        <DialogContent sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
          <TextField
            label="Trip Name"
            value={editTrip?.trip_name || ''}
            onChange={(e) => setEditTrip({ ...editTrip, trip_name: e.target.value })}
          />
          <TextField
            label="Start Date"
            type="date"
            InputLabelProps={{ shrink: true }}
            value={editTrip?.start_date?.split('T')[0] || ''}
            onChange={(e) => setEditTrip({ ...editTrip, start_date: e.target.value })}
          />
          <TextField
            label="End Date"
            type="date"
            InputLabelProps={{ shrink: true }}
            value={editTrip?.end_date?.split('T')[0] || ''}
            onChange={(e) => setEditTrip({ ...editTrip, end_date: e.target.value })}
          />

          <Divider sx={{ my: 2 }} />
          <Typography variant="subtitle1">Edit Travellers:</Typography>
          {editTrip?.group_members.map((member, idx) => (
            <Box key={idx} sx={{ display: 'flex', gap: 2 }}>
              <TextField
                label="Name"
                value={member.name}
                onChange={(e) => {
                  const updated = [...editTrip.group_members];
                  updated[idx].name = e.target.value;
                  setEditTrip({ ...editTrip, group_members: updated });
                }}
              />
              <TextField
                label="Email"
                value={member.email}
                onChange={(e) => {
                  const updated = [...editTrip.group_members];
                  updated[idx].email = e.target.value;
                  setEditTrip({ ...editTrip, group_members: updated });
                }}
              />
              <IconButton onClick={() => {
                setEditTrip((prev) => ({
                  ...prev,
                  group_members: prev.group_members.filter((_, i) => i !== idx)
                }));
              }}>
                <RemoveCircle />
              </IconButton>
            </Box>
          ))}
          <Button
            variant="outlined"
            startIcon={<AddCircle />}
            onClick={() =>
              setEditTrip((prev) => ({
                ...prev,
                group_members: [...prev.group_members, { name: '', email: '' }]
              }))
            }
          >
            Add Traveller
          </Button>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditTrip(null)}>Cancel</Button>
          <Button variant="contained" onClick={handleEditSave}>Save</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Dashboard;
