import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Card,
  CardContent,
  Grid,
  Button
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [trips, setTrips] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const customer_id = localStorage.getItem('customer_id');
    if (!customer_id) {
      alert('Please login first');
      navigate('/login');
      return;
    }

    fetch(`http://localhost:5000/trips/${customer_id}`)
      .then((res) => res.json())
      .then((data) => setTrips(data))
      .catch((err) => {
        console.error('Failed to fetch trips:', err);
        alert('Error loading your projects');
      });
  }, [navigate]);

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>Your Projects</Typography>
      <Grid container spacing={3}>
        {trips.length === 0 ? (
          <Typography>No projects found. Click below to create your first one.</Typography>
        ) : (
          trips.map((trip) => (
            <Grid item xs={12} sm={6} md={4} key={trip.trip_id}>
              <Card elevation={3}>
                <CardContent>
                  <Typography variant="h6">{trip.trip_type || 'Untitled Trip'}</Typography>
                  <Typography variant="body2">
                    {trip.start_date} â†’ {trip.end_date}
                  </Typography>
                  <Typography variant="caption" color="textSecondary">
                    Trip ID: {trip.trip_id}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))
        )}
      </Grid>

      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 4 }}
        onClick={() => navigate('/create-project')}
      >
        Create New Project
      </Button>
    </Container>
  );
};

export default Dashboard;
