import React, { useState } from 'react';

const CreateProjectPage = () => {
  const [trip, setTrip] = useState({ start_date: '', end_date: '', trip_type: '' });

  const handleChange = e => {
    setTrip({ ...trip, [e.target.name]: e.target.value });
  };

  const handleCreate = async () => {
    const customer = JSON.parse(localStorage.getItem('customer'));
    const res = await fetch('http://localhost:5000/create-trip', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...trip, customer_id: customer.customer_id })
    });

    const data = await res.json();
    if (res.ok) {
      alert('Trip created!');
      window.location.href = '/dashboard';
    } else {
      alert(data.error || 'Failed to create trip');
    }
  };

  return (
    <div>
      <h2>Create Trip</h2>
      <input name="start_date" type="date" onChange={handleChange} />
      <input name="end_date" type="date" onChange={handleChange} />
      <input name="trip_type" placeholder="Trip Type" onChange={handleChange} />
      <button onClick={handleCreate}>Save</button>
    </div>
  );
};

export default CreateProjectPage;
